import React, { useState } from "react";
import "./ExpenseForm.css";

const ExpenseForm = () => {
  // const [enteredTitle, setEnteredTitle] = useState("");
  // const [enteredAmount, setenteredAmount] = useState("");
  // const [enteredDate, setEnteredDate] = useState("");
const [userInput, setUserInput] = useState({
  enteredTitle: '',
  enteredAmount: '',
  enteredDate: ''
});

  const titleChangeHandler = (event) => {
    // Method 1:
    // setEnteredTitle(event.target.value);
    // Method 2:
    // setUserInput({
    //   ...userInput, // pull out all key-value pairs from the old object
    //   enteredTitle: event.target.value // overwrite the key-value pair you need to change
    // });
    // Method 3:
    setUserInput( (prevState) => {
      return {...prevState, enteredTitle: event.target.value};
    })
  };
  const amountChangeHandler = (event) => {
    // setenteredAmount(event.target.value); // the value is always a string
    // setUserInput({
    //   ...userInput,
    //   enteredAmount: event.target.value
    // })
    setUserInput( (prevState) => {
      return {...prevState, enteredAmount:event.target.value };
    })
  };
  const dateChangeHandler = (event) => {
    // setEnteredDate(event.target.value);
    // setUserInput({
    //   ...userInput,
    //   enteredDate: event.target.value
    // })
    setUserInput( (prevState) => {
      return {...prevState, enteredDate:event.target.value};
    })
  };

  return (
    <form>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input type="text" onChange={titleChangeHandler} />
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input
            type="text"
            min="0.01"
            step="0.01"
            onChange={amountChangeHandler}
          />
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input
            type="date"
            min="2019-01-01"
            max="2022-12-31"
            onChange={dateChangeHandler}
          />
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="submit">Add Expense</button>
      </div>
    </form>
  );
};
export default ExpenseForm;
